import React from "react";
import { formatDistanceToNow,differenceInDays } from "date-fns";
import "./app.css";

const Task = ({ taskObj, onComplete }) => {
const formattedDeadline= formatDistanceToNow(new Date(taskObj.deadline));
const daysUnitDeadline = differenceInDays(new Date(taskObj.deadline),new Date());
const taskClasses = daysUnitDeadline <= 3 ? "urgent" : "normal";


  return (
    <div className={taskClasses}>
      <h3 className="task h3">{taskObj.title}</h3>
      <div className="deadline">son teslim: <span className="deadline span">{taskObj.deadline}</span></div>
      <p className="task p">{taskObj.description}</p>
      <div>
        {taskObj.people.map((p) => (
          <span className="pill" key={p}>{p}</span>
        ))}
      </div>
      {onComplete && <button className="task button" onClick={() => onComplete(taskObj.id)}>TamamlandÄ±</button>}
    </div>
  );
};

export default Task;
