import React from "react";
import { formatDistanceToNow, differenceInDays } from "date-fns";
import { tr } from "date-fns/locale";
import "./app.css";

const Task = ({ taskObj, onComplete }) => {
  const formattedDeadline = formatDistanceToNow(new Date(taskObj.deadline), { locale: tr });
  const daysUntilDeadline = differenceInDays(new Date(taskObj.deadline), new Date());

  return (
    <div className="task">
      <h3>{taskObj.title}</h3>
      <div className={`deadline ${daysUntilDeadline <= 3 ? "urgent" : "normal"}`}>son teslim: <span>{formattedDeadline} kaldı</span></div>
      <p>{taskObj.description}</p>
      <div>
        {taskObj.people.map((p) => (
          <span className="pill" key={p}>{p}</span>
        ))}
      </div>
      {onComplete && <button onClick={() => onComplete(taskObj.id)}>Tamamlandı</button>}
    </div>
  );
};

export default Task;
