import React from "react";
import { formatDistanceToNow,differenceInMinutes } from "date-fns";
import { tr } from "date-fns/locale";

const Task = ({ taskObj, onComplete }) => {
  const formattedDeadline = formatDistanceToNow(new Date(taskObj.deadline), { locale: tr });
  const minutesUntilDeadline = differenceInMinutes(new Date(taskObj.deadline), new Date());
 const hoursUntilDeadline = Math.floor(minutesUntilDeadline / 60);
 const remainingMinutes = minutesUntilDeadline % 60;


  return (
    <div className="p-6 bg-white rounded shadow task">
      <h3 className="text-orange-500">{taskObj.title}</h3>
      <div className="deadline">
        Son teslim:{" "}
        <span className={`deadline-span ${hoursUntilDeadline <= 3 ? "bg-red-100" : "bg-blue-300" }`}>
  {hoursUntilDeadline} hours {remainingMinutes} minutes left
</span>
      </div>
      <p>{taskObj.description}</p>
      <div>
        {taskObj.people.map((p) => (
          <span className="pill" key={p}>
            {p}
          </span>
        ))}
      </div>
      {onComplete && (
        <button
          className="block mt-4 px-4 py-2 mx-auto bg-orange-300 rounded shadow"
          onClick={() => onComplete(taskObj.id)}
        >
          TamamlandÄ±
        </button>
      )}
    </div>
  );
};

export default Task;
